    function set_menu() {
        
        global $wpdb;
        $table_db_name = $wpdb->prefix . "terms";
        $rows = $wpdb->get_results("SELECT * FROM $table_db_name where name='Primary Menu'",ARRAY_A);
        $menu_ids = array();
        foreach($rows as $row) {
            $menu_ids[$row["name"]] = $row["term_id"] ;
        }
        
        if ( has_nav_menu( 'primary' ) ) {
             //Do something
        }
        else {
            set_theme_mod( 'nav_menu_locations', array_map( 'absint', array(   'primary' =>$menu_ids['Primary Menu'] ) ) );
        }   
    }

