    function recursive_array_replace($find, $replace, $array){
        $newArray = array();
        foreach ($array as $key => $value) {
        if($key == 'nav_menu_locations'){
            foreach ($array['nav_menu_locations'] as $keys => $value2) {
                $newArray['nav_menu_locations'][$keys] = str_replace($find,$replace , $value2);
            }
        }
        if (!is_array($value)) {
            
           if($key == 'header_image_data'){
                foreach ($array['header_image_data'] as $keys => $value2) {
                    $newArray['header_image_data'][$keys] = str_replace($find,$replace , $value2);
                }
            }
            else{
                $newArray[$key] = str_replace($find,$replace , $value);        
            }
            
        }else{
            $newArray[$key] = $this->recursive_array_replace($find, $replace, $value);
        }
        return $newArray;
        }
    }

